import React from "react";

// We'll use ethers to interact with the Ethereum network and our contract
import { ethers } from "ethers";

// We import the contract's artifacts and address here, as we are going to be
// using them with ethers
import contractABI from "../contracts/StableCoin-abi.json";
import contractADR from "../contracts/StableCoin-adr.json";

// All the logic of this dapp is contained in the Dapp component.
// These other components are just presentational ones: they don't have any
// logic. They just render HTML.
import { NoWalletDetected } from "./NoWalletDetected";
import { ConnectWallet } from "./ConnectWallet";
import { Loading } from "./Loading";
import { Transfer } from "./Transfer";
import { Allowance } from "./Allowance";
import { TransactionErrorMessage } from "./TransactionErrorMessage";
import { WaitingForTransactionMessage } from "./WaitingForTransactionMessage";
import { NoTokensMessage } from "./NoTokensMessage";


// This is the Hardhat Network id, you might change it in the hardhat.config.js
// Here's a list of network ids https://docs.metamask.io/guide/ethereum-provider.html#properties
// to use when deploying to other networks.
const HARDHAT_NETWORK_ID = '31337';

// This is an error code that indicates that the user canceled a transaction
const ERROR_CODE_TX_REJECTED_BY_USER = 4001;

// This component is in charge of doing these things:
//   1. It connects to the user's wallet
//   2. Initializes ethers and the Token contract
//   3. Polls the user balance to keep it updated.
//   4. Transfers tokens by sending transactions
//   5. Renders the whole application
//
// Note that (3) and (4) are specific of this sample application, but they show
// you how to keep your Dapp and contract's state in sync,  and how to send a
// transaction.
export class Dapp extends React.Component {
  constructor(props) {
    super(props);

    // We store multiple things in Dapp's state.
    // You don't need to follow this pattern, but it's an useful example.
    this.initialState = {
      tokenData: undefined,
      total: undefined,
      selectedAddress: undefined,
      selectedName: undefined,
      balance: undefined,
      allowance: undefined,
      txBeingSent: undefined,
      transactionError: undefined,
      networkError: undefined,
      accountError: undefined,
    };

    this.state = this.initialState;
  }



  render() {
    // Ethereum wallets inject the window.ethereum object. If it hasn't been
    // injected, we instruct the user to install MetaMask.
    if (window.ethereum === undefined) {
      return <NoWalletDetected />;
    }

    // The next thing we need to do, is to ask the user to connect their wallet.
    // When the wallet gets connected, we are going to save the users's address
    // in the component's state. So, if it hasn't been saved yet, we have
    // to show the ConnectWallet component.
    //
    // Note that we pass it a callback that is going to be called when the user
    // clicks a button. This callback just calls the _connectWallet method.
    if (!this.state.selectedAddress) {
      return (
        <ConnectWallet
          connectWallet={() => this._connectWallet()}
          networkError={this.state.networkError}
          dismiss={() => this._dismissNetworkError()}
        />
      );
    }
    console.log(this.state.selectedAddress)
    if ((this.state.selectedAddress !== "0x559bc07434c89c5496d790dfd2885dc966f9113a") && 
        (this.state.selectedAddress !== "0x6dc89393fa30b64c56deff31daacf10cedcd852d") && 
        (this.state.selectedAddress !== "0x54806dd512b21814aa560d627432a75720ed6bb3")) {
      return (
        <ConnectWallet
          connectWallet={() => this._connectWallet()}
          networkError={this.state.accountError}
          dismiss={() => this._dismissAccountError()}
        />
      );
    }


    // If the token data or the user's balance hasn't loaded yet, we show
    // a loading component.
    if (!this.state.tokenData || !this.state.balance) {
      return <Loading />;
    }

    if(this.state.selectedName === "party1") {
      return (
        <div className="p-4">
          <div className="row">
            <div className="col-12 card">
              <h1>
                Contract Info
              </h1>
              <p>
                Token:  <b>{this.state.tokenData.name}</b>
              </p>
              <p>
                Symbol:  <b>{this.state.tokenData.symbol}</b>
              </p>
              <p>
                Address:  <b>{this._token.address}</b>
              </p>
              <p>
                TotalSupply:  <b>$ {(Math.round(this.state.tokenData.total * this.state.tokenData.decimals**10) / (this.state.tokenData.decimals**10)).toFixed(this.state.tokenData.decimals).toString()}</b>
              </p>

            </div>
          </div>


          <div className="row">
          <div className="col-4 card">
              <h2>
                Wallet
              </h2>
              <p>
                Holder's name:  <b>{this.state.selectedName}</b>
              </p>
              <p>
                Address:  <b>{this.state.selectedAddress}</b>
              </p>
              <p>
                Balance:  <b>$ {(Math.round(this.state.balance * this.state.tokenData.decimals**10) / (this.state.tokenData.decimals**10)).toFixed(this.state.tokenData.decimals).toString()}</b>
              </p>
              <p>
                Allowed:  <b>$ {(Math.round(this.state.allowance * this.state.tokenData.decimals**10) / (this.state.tokenData.decimals**10)).toFixed(this.state.tokenData.decimals).toString()}</b>
              </p>
            </div>
            <div className="col-4">
