import React from "react";

export function Allowance({ setAllowance }) {
  return (
    <div>
      <h4>Set Allowance</h4>
      <form
        onSubmit={(event) => {
          // This function just calls the transferTokens callback with the
          // form's data.
          event.preventDefault();

          const formData = new FormData(event.target);
          const amount = formData.get("amount");
          const owner = formData.get("owner");
          const spender = formData.get("spender");

          if (owner && spender && amount) {
            setAllowance(owner, spender);
          }
        }}
      >
        <div className="form-group">
          <label>Amount</label>
          <input
            className="form-control"
            type="number"
            step="1"
            name="amount"
            placeholder="1"
            required
          />
        </div>
        <div className="form-group">
          <label>owner</label>
          <input className="form-control" type="text" name="owner" required />
        </div>
        <div className="form-group">
          <label>spender</label>
          <input className="form-control" type="text" name="spender" required />
        </div>
        <div className="form-group">
          <input className="btn btn-primary" type="submit" value="setAllowance" />
        </div>
      </form>
    </div>
  );
}
