import React from "react";

import { Transfer } from "./Transfer";
import { Allowance } from "./Allowance";
import { TransactionErrorMessage } from "./TransactionErrorMessage";
import { WaitingForTransactionMessage } from "./WaitingForTransactionMessage";
import { NoTokensMessage } from "./NoTokensMessage";

export function Party() {
  return (

<div>
    <div className="row">
    <div className="col-4">
        <h2>
          Wallet
        </h2>
        <p>
          Holder's name:  <b>{this.state.selectedName}</b>
        </p>
        <p>
          Address:  <b>{this.state.selectedAddress}</b>
        </p>
        <p>
          Balance:  <b>$ {(Math.round(this.state.balance * this.state.tokenData.decimals**10) / (this.state.tokenData.decimals**10)).toFixed(this.state.tokenData.decimals).toString()}</b>
        </p>
        <p>
          Allowed:  <b>$ {(Math.round(this.state.allowance * this.state.tokenData.decimals**10) / (this.state.tokenData.decimals**10)).toFixed(this.state.tokenData.decimals).toString()}</b>
        </p>
      </div>
      <div className="col-4">
        {/* 
          Sending a transaction isn't an immidiate action. You have to wait
          for it to be mined.
          If we are waiting for one, we show a message here.
        */}
        {this.state.txBeingSent && (
          <WaitingForTransactionMessage txHash={this.state.txBeingSent} />
        )}

        {/* 
          Sending a transaction can fail in multiple ways. 
          If that happened, we show a message here.
        */}
        {this.state.transactionError && (
          <TransactionErrorMessage
            message={this._getRpcErrorMessage(this.state.transactionError)}
            dismiss={() => this._dismissTransactionError()}
          />
        )}
      </div>
    </div>

    <div className="row">
      <div className="col-4">
        {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
        {this.state.balance.eq(0) && (
          <NoTokensMessage selectedAddress={this.state.selectedAddress} />
        )}

        {/*
          This component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
        {this.state.balance.gt(0) && (
          <Transfer
            transferTokens={(to, amount) =>
              this._transferTokens(to, amount)
            }
            tokenSymbol={this.state.tokenData.symbol}
          />
        )}
      </div>
    </div>

    <div className="row">
      <div className="col-4">
        {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
        {this.state.balance.eq(0) && (
          <NoTokensMessage selectedAddress={this.state.selectedAddress} />
        )}

        {/*
          This component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
        {this.state.balance.gt(0) && (
          <Allowance
            approve={(spender, amount) =>
              this._setAllowance(spender, amount)
            }
            tokenSymbol={this.state.tokenData.symbol}
          />
        )}
      </div>
      <div className="row">
        <div className="col-4">
          {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
          {this.state.balance.eq(0) && (
            <NoTokensMessage selectedAddress={this.state.selectedAddress} />
          )}

          {/*
          This component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
          {this.state.balance.gt(0) && (
            <Allowance
              approve={(spender, amount) =>
                this._setAllowance(spender, amount)
              }
              tokenSymbol={this.state.tokenData.symbol}
            />
          )}
        </div>
        <div className="col-4">
          {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
          {this.state.balance.eq(0) && (
            <NoTokensMessage selectedAddress={this.state.selectedAddress} />
          )}

          {/*
          This component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
          {this.state.balance.gt(0) && (
            <Allowance
              approve={(spender, amount) =>
                this._setAllowance(spender, amount)
              }
              tokenSymbol={this.state.tokenData.symbol}
            />
          )}
        </div>
      </div>
    </div>
 </div>
  );
}
