import React from "react";

export function Contract({ tokenData }) {
    return (
        <div className="container"
            style={{
                background: "rgba(255, 255, 255, 0.5)",
            }}
        >
            <h1>
                Contract Info
              </h1>
            <p>
                Token:  <b>{tokenData.name}</b>
            </p>
            <p>
                Symbol:  <b>{tokenData.symbol}</b>
            </p>
            <p>
                Address:  <b>{tokenData.address}</b>
            </p>
            <p>
                TotalSupply:  <b>$ {(Math.round(tokenData.total * tokenData.decimals ** 10) / (tokenData.decimals ** 10)).toFixed(tokenData.decimals).toString()}</b>
            </p>

        </div>
    );
}
