import React from "react";

// We'll use ethers to interact with the Ethereum network and our contract
import { ethers } from "ethers";

// We import the contract's artifacts and address here, as we are going to be
// using them with ethers
import ABI from "../contract/abi.json";
import ADR from "../contract/adr.json";
import ACC from "../contract/acc.json";

// All the logic of this dapp is contained in the Dapp component.
// These other components are just presentational ones: they don't have any
// logic. They just render HTML.
import { NoWalletDetected } from "./NoWalletDetected";
import { ConnectWallet } from "./ConnectWallet";
import { Loading } from "./Loading";
import { Transfer } from "./Transfer";
import { Allowance } from "./Allowance";
import { TransactionErrorMessage } from "./TransactionErrorMessage";
import { WaitingForTransactionMessage } from "./WaitingForTransactionMessage";
import { NoTokensMessage } from "./NoTokensMessage";
import { Contract } from "./Contract";


// This is the Hardhat Network id, you might change it in the hardhat.config.js
// Here's a list of network ids https://docs.metamask.io/guide/ethereum-provider.html#properties
// to use when deploying to other networks.
const NETWORK_ID = '4';  // RINKEBY
// const NETWORK_ID = '31337';  // HARDHAT


const ERROR_CODE_TX_REJECTED_BY_USER = 4001;

// This component is in charge of doing these things:
//   1. It connects to the user's wallet
//   2. Initializes ethers and the Token contract
//   3. Polls the user balance to keep it updated.
//   4. Transfers tokens by sending transactions
//   5. Renders the whole application
//
// Note that (3) and (4) are specific of this sample application, but they show
// you how to keep your Dapp and contract's state in sync,  and how to send a
// transaction.
export class Dapp extends React.Component {
  constructor(props) {
    super(props);

    // We store multiple things in Dapp's state.
    // You don't need to follow this pattern, but it's an useful example.
    this.initialState = {
      tokenData: undefined,
      total: undefined,
      selectedAddress: undefined,
      selectedName: undefined,
      balance: undefined,
      allowance: undefined,
      txBeingSent: undefined,
      transactionError: undefined,
      networkError: undefined,
      accountError: undefined,
    };

    this.state = this.initialState;
  }



  render() {

    if (window.ethereum === undefined) {
      return <NoWalletDetected />;
    }

    if (!this.state.selectedAddress) {
      return (
        <ConnectWallet
          connectWallet={() => this._connectWallet()}
          networkError={this.state.networkError}
          dismiss={() => this._dismissNetworkError()}
        />
      );
    }

    if ((this.state.selectedAddress !== this.state.accounts.controller) &&
      (this.state.selectedAddress !== this.state.accounts.party1) &&
      (this.state.selectedAddress !== this.state.accounts.controller)) {
      return (
        <ConnectWallet
          connectWallet={() => this._connectWallet()}
          accountError={this.state.accountError}
          dismiss={() => this._dismissAccountError()}
        />
      );
    }


    if (!this.state.tokenData || !this.state.balance) {
      return <Loading />;
    }

    if (this.state.selectedName === "controller") {
      return (
        <div className="">
          <div className="row">
            <Contract tokenData={this.state.tokenData} />
          </div>


          <div className="row">
            <div className="container p-3">
              <h2>Wallet</h2>
              <p>
                Holder's name:  <b>{this.state.selectedName}</b>
              </p>
              <p>
                Address:  <b>{this.state.selectedAddress}</b>
              </p>
              <p>
                Balance:  <b>$ {(Math.round(this.state.balance * this.state.tokenData.decimals ** 10) / (this.state.tokenData.decimals ** 10)).toFixed(this.state.tokenData.decimals).toString()}</b>
              </p>
              <p>
                Allowed:  <b>$ {(Math.round(this.state.allowance * this.state.tokenData.decimals ** 10) / (this.state.tokenData.decimals ** 10)).toFixed(this.state.tokenData.decimals).toString()}</b>
              </p>
            </div>
            <div>
              {this.state.txBeingSent && (<WaitingForTransactionMessage txHash={this.state.txBeingSent} />)}
              {this.state.transactionError && (
                <TransactionErrorMessage
                  message={this._getRpcErrorMessage(this.state.transactionError)}
                  dismiss={() => this._dismissTransactionError()}
                />)}

            </div>
          </div>

          <div className="row">
            <div className="col-4">
              {/*
                If the user has no tokens, we don't show the Tranfer form
              */}
              {this.state.balance.eq(0) && (
                <NoTokensMessage selectedAddress={this.state.selectedAddress} />
              )}

              {/*
                This component displays a form that the user can use to send a 
                transaction and transfer some tokens.
                The component doesn't have logic, it just calls the transferTokens
                callback.
              */}
              {this.state.balance.gt(0) && (
                <Transfer
                  transferTokens={(to, amount) =>
                    this._transferTokens(to, amount)
                  }
                  tokenSymbol={this.state.tokenData.symbol}
                />
              )}
            </div>
          </div>

          <div className="row card">
            <div className="col-4">
              {/*
                If the user has no tokens, we don't show the Tranfer form
              */}
              {this.state.balance.eq(0) && (
                <NoTokensMessage selectedAddress={this.state.selectedAddress} />
              )}

              {this.state.balance.gt(0) && (
                <Allowance
                  approve={(spender, amount) =>
                    this._setAllowance(spender, amount)
                  }
                  tokenSymbol={this.state.tokenData.symbol}
                />
              )}
            </div>
            <div className="row">
              <div className="col-4">

                {this.state.balance.eq(0) && (
                  <NoTokensMessage selectedAddress={this.state.selectedAddress} />
                )}


              </div>
            </div>
          </div>
        </div>
      );


    } else {
      return (
        <div className="">
          <div className="row">
            <Contract tokenData={this.state.tokenData} />
          </div>


          <div className="row">
            <div className="container p-3">
              <h2>Wallet</h2>
              <div className="row">
                <div className="col-6">
                  <p>Name:</p>
                  <p>Address:</p>
                  <p>Balance:</p>
                  <p>Approved:</p>
                </div>
                <div className="col-6">
                  <p><b>{this.state.selectedName}</b></p>
                  <p><b>{this.state.selectedAddress}</b></p>
                  <p><b>$ {(Math.round(this.state.balance * this.state.tokenData.decimals ** 10) / (this.state.tokenData.decimals ** 10)).toFixed(this.state.tokenData.decimals).toString()}</b></p>
                  <p><b>$ {(Math.round(this.state.allowance * this.state.tokenData.decimals ** 10) / (this.state.tokenData.decimals ** 10)).toFixed(this.state.tokenData.decimals).toString()}</b></p>
                </div>
              </div>
            </div>
            <div>

              {this.state.txBeingSent && (<WaitingForTransactionMessage txHash={this.state.txBeingSent} />)}

              {this.state.transactionError && (
                <TransactionErrorMessage
                  message={this._getRpcErrorMessage(this.state.transactionError)}
                  dismiss={() => this._dismissTransactionError()}
                />)}
            </div>
          </div>

          <div className="row">
            <div className="col-4">
              {/*
                If the user has no tokens, we don't show the Tranfer form
              */}
              {this.state.balance.eq(0) && (
                <NoTokensMessage selectedAddress={this.state.selectedAddress} />
              )}

              {/*
                This component displays a form that the user can use to send a 
                transaction and transfer some tokens.
                The component doesn't have logic, it just calls the transferTokens
                callback.
              */}
              {this.state.balance.gt(0) && (
                <Transfer
                  transferTokens={(to, amount) =>
                    this._transferTokens(to, amount)
                  }
                  tokenSymbol={this.state.tokenData.symbol}
                />
              )}
            </div>
          </div>

          <div className="row card">
            <div className="col-4">
              {/*
                If the user has no tokens, we don't show the Tranfer form
              */}
              {this.state.balance.eq(0) && (
                <NoTokensMessage selectedAddress={this.state.selectedAddress} />
              )}

              {this.state.balance.gt(0) && (
                <Allowance
                  approve={(spender, amount) =>
                    this._setAllowance(spender, amount)
                  }
                  tokenSymbol={this.state.tokenData.symbol}
                />
              )}
            </div>
            <div className="row">
              <div className="col-4">

                {this.state.balance.eq(0) && (
                  <NoTokensMessage selectedAddress={this.state.selectedAddress} />
                )}


              </div>
            </div>
          </div>
        </div>
      );

    }
  }



  componentWillUnmount() {
    // We poll the user's balance, so we have to stop doing that when Dapp
    // gets unmounted
    this._stopPollingData();
  }

  async _connectWallet() {
    // This method is run when the user clicks the Connect. It connects the
    // dapp to the user's wallet, and initializes it.

    // To connect to the user's wallet, we have to run this method.
    // It returns a promise that will resolve to the user's address.
    const [selectedAddress] = await window.ethereum.enable();
    let check = await this._initialize(selectedAddress);
    // Once we have the address, we can initialize the application.

    // First we check the network
    if (!this._checkNetwork() && await check) {
      return;
    }



    if (!this._checkAccounts()  && await check) {
      console.log("check1")
      return;
    }

    // We reinitialize it whenever the user changes their account.
    window.ethereum.on("accountsChanged", ([newAddress]) => {
      this._stopPollingData();

      if (newAddress === undefined) {
        return this._resetState();
      }

      this._initialize(newAddress);
    });

    window.ethereum.on("networkChanged", ([networkId]) => {
      this._stopPollingData();
      this._resetState();
    });
  }

  async _initialize(userAddress) {

    let accounts = ACC.accounts
    let wallet = {}
    let selectedName = undefined
    Object.keys(accounts).forEach(key => {
      if(userAddress.toLowerCase() === key.toLowerCase()) {
        selectedName = accounts[key]
        wallet[accounts[key]] = key
      } else {
        wallet[accounts[key]] = key
      }
    });



    this.setState({
      accounts: wallet,
      selectedName: selectedName,
      selectedAddress: userAddress,
    });



    this._intializeEthers();
    this._getTokenData();
    this._startPollingData();
    return 1
  }

  async _intializeEthers() {
    this._provider = new ethers.providers.Web3Provider(window.ethereum);

    this._token = new ethers.Contract(
      ADR.contract,
      ABI.abi,
      this._provider.getSigner(0)
    );


  }

  _startPollingData() {
    this._pollDataInterval = setInterval(() => this._updateAccount(), 1000);

    this._updateAccount();

  }

  _stopPollingData() {
    clearInterval(this._pollDataInterval);
    this._pollDataInterval = undefined;
  }

  async _getTokenData() {
    const name = await this._token.name();
    const symbol = await this._token.symbol();
    const decimals = await this._token.decimals();
    const total = await this._token.totalSupply();
    const address = await this._token.address;

    this.setState({ tokenData: { name, symbol, decimals, total, address } });
  }

  async _updateAccount() {
    const balance = await this._token.balanceOf(this.state.selectedAddress);
    await this.setState({ balance });

    const allowance = await this._token.allowance(this.state.selectedAddress, this.state.accounts.controller);
    await this.setState({ allowance });

  }

  async _transferTokens(to, amount) {
    try {
      // If a transaction fails, we save that error in the component's state.
      // We only save one such error, so before sending a second transaction, we
      // clear it.
      this._dismissTransactionError();

      // We send the transaction, and save its hash in the Dapp's state. This
      // way we can indicate that we are waiting for it to be mined.
      const tx = await this._token.transfer(to, amount);
      this.setState({ txBeingSent: tx.hash });

      // We use .wait() to wait for the transaction to be mined. This method
      // returns the transaction's receipt.
      const receipt = await tx.wait();

      // The receipt, contains a status flag, which is 0 to indicate an error.
      if (receipt.status === 0) {
        // We can't know the exact error that make the transaction fail once it
        // was mined, so we throw this generic one.
        throw new Error("Transaction failed");
      }

      // If we got here, the transaction was successful, so you may want to
      // update your state. Here, we update the user's balance.
      await this._updateAccount();
    } catch (error) {
      // REJECTED TX BY USER
      if (error.code === ERROR_CODE_TX_REJECTED_BY_USER) {
        return;
      }

      // Other errors are logged and stored in the Dapp's state. This is used to
      // show them to the user, and for debugging.
      console.error(error);
      this.setState({ transactionError: error });
    } finally {
      // If we leave the try/catch, we aren't sending a tx anymore, so we clear
      // this part of the state.
      this.setState({ txBeingSent: undefined });
    }
  }

  async _setAllowance(spender, amount) {

    try {
      // If a transaction fails, we save that error in the component's state.
      // We only save one such error, so before sending a second transaction, we
      // clear it.
      this._dismissTransactionError();

      // We send the transaction, and save its hash in the Dapp's state. This
      // way we can indicate that we are waiting for it to be mined.
      const tx2 = await this._token.approve(spender, amount);
      this.setState({ txBeingSent: tx2.hash });

      // We use .wait() to wait for the transaction to be mined. This method
      // returns the transaction's receipt.
      const receipt = await tx2.wait();

      if (receipt.status === 0) {
        throw new Error("Transaction failed");
      }

      await this._updateAccount();
    } catch (error) {
      // REJECTED TX BY USER
      if (error.code === ERROR_CODE_TX_REJECTED_BY_USER) {
        return;
      }

      // Other errors are logged and stored in the Dapp's state. This is used to
      // show them to the user, and for debugging.
      console.error(error);
      this.setState({ transactionError: error });
    } finally {
      // If we leave the try/catch, we aren't sending a tx anymore, so we clear
      // this part of the state.
      this.setState({ txBeingSent: undefined });
    }
  }

  // This method just clears part of the state.
  _dismissTransactionError() {
    this.setState({ transactionError: undefined });
  }

  // This method just clears part of the state.
  _dismissNetworkError() {
    this.setState({ networkError: undefined });
  }

  _dismissAccountError() {
    this.setState({ accountError: undefined });
  }
  // This is an utility method that turns an RPC error into a human readable
  // message.
  _getRpcErrorMessage(error) {
    if (error.data) {
      return error.data.message;
    }

    return error.message;
  }

  // This method resets the state
  _resetState() {
    this.setState(this.initialState);
  }

  // This method checks if Metamask selected network is Localhost:8545 
  _checkNetwork() {
    if (window.ethereum.networkVersion === NETWORK_ID) {
      return true;
    }

    this.setState({
      networkError: `Please connect Metamask to network: ${NETWORK_ID}`
    });

    return false;
  }

  // This method checks if Metamask selected network is Localhost:8545 
  _checkAccounts() {

    if ((this.state.selectedAddress === this.state.accounts.controller) ||
        (this.state.selectedAddress === this.state.accounts.party1) ||
        (this.state.selectedAddress === this.state.accounts.party2)) {
      return true;
    }
    this.setState({
      accountError: 'Please use one of the first 3 accounts provided by the mnemonic in .env '
    });

    return false;
  }
}
