import React from "react";

import { Transfer } from "./Transfer";
import { Allowance } from "./Allowance";
import { TransactionErrorMessage } from "./TransactionErrorMessage";
import { WaitingForTransactionMessage } from "./WaitingForTransactionMessage";
import { NoTokensMessage } from "./NoTokensMessage";

export function Party({state}) {
  return (

<div>
<div className="row">
          <div className="col-12 card">
            <h1>
              Contract Info
            </h1>
            <p>
              Token:  <b>{state.tokenData.name}</b>
            </p>
            <p>
              Symbol:  <b>{state.tokenData.symbol}</b>
            </p>
            <p>
              Address:  <b>{_token.address}</b>
            </p>
            <p>
              TotalSupply:  <b>$ {(Math.round(state.tokenData.total * state.tokenData.decimals**10) / (state.tokenData.decimals**10)).toFixed(state.tokenData.decimals).toString()}</b>
            </p>

          </div>
        </div>
    <div className="row">
    <div className="col-4">
        <h2>
          Wallet
        </h2>
        <p>
          Holder's name:  <b>{state.selectedName}</b>
        </p>
        <p>
          Address:  <b>{state.selectedAddress}</b>
        </p>
        <p>
          Balance:  <b>$ {(Math.round(state.balance * state.tokenData.decimals**10) / (state.tokenData.decimals**10)).toFixed(state.tokenData.decimals).toString()}</b>
        </p>
        <p>
          Allowed:  <b>$ {(Math.round(state.allowance * state.tokenData.decimals**10) / (state.tokenData.decimals**10)).toFixed(state.tokenData.decimals).toString()}</b>
        </p>
      </div>
      <div className="col-4">
        {/* 
          Sending a transaction isn't an immidiate action. You have to wait
          for it to be mined.
          If we are waiting for one, we show a message here.
        */}
        {state.txBeingSent && (
          <WaitingForTransactionMessage txHash={state.txBeingSent} />
        )}

        {/* 
          Sending a transaction can fail in multiple ways. 
          If that happened, we show a message here.
        */}
        {state.transactionError && (
          <TransactionErrorMessage
            message={_getRpcErrorMessage(state.transactionError)}
            dismiss={() => _dismissTransactionError()}
          />
        )}
      </div>
    </div>

    <div className="row">
      <div className="col-4">
        {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
        {state.balance.eq(0) && (
          <NoTokensMessage selectedAddress={state.selectedAddress} />
        )}

        {/*
        component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
        {state.balance.gt(0) && (
          <Transfer
            transferTokens={(to, amount) =>
              _transferTokens(to, amount)
            }
            tokenSymbol={state.tokenData.symbol}
          />
        )}
      </div>
    </div>

    <div className="row">
      <div className="col-4">
        {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
        {state.balance.eq(0) && (
          <NoTokensMessage selectedAddress={state.selectedAddress} />
        )}

        {/*
        component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
        {state.balance.gt(0) && (
          <Allowance
            approve={(spender, amount) =>
              _setAllowance(spender, amount)
            }
            tokenSymbol={state.tokenData.symbol}
          />
        )}
      </div>
      <div className="row">
        <div className="col-4">
          {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
          {state.balance.eq(0) && (
            <NoTokensMessage selectedAddress={state.selectedAddress} />
          )}

          {/*
        component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
          {state.balance.gt(0) && (
            <Allowance
              approve={(spender, amount) =>
                _setAllowance(spender, amount)
              }
              tokenSymbol={state.tokenData.symbol}
            />
          )}
        </div>
        <div className="col-4">
          {/*
          If the user has no tokens, we don't show the Tranfer form
        */}
          {state.balance.eq(0) && (
            <NoTokensMessage selectedAddress={state.selectedAddress} />
          )}

          {/*
        component displays a form that the user can use to send a 
          transaction and transfer some tokens.
          The component doesn't have logic, it just calls the transferTokens
          callback.
        */}
          {state.balance.gt(0) && (
            <Allowance
              approve={(spender, amount) =>
                _setAllowance(spender, amount)
              }
              tokenSymbol={state.tokenData.symbol}
            />
          )}
        </div>
      </div>
    </div>
 </div>
  );
}
